mapper.select:
SELECT GPIDX,SUBJECT,CONTENT,CHATURL,PROGIDX,ID,MAXUSER,CURRUSER,DEADLINE,GPCTGIDX,reg_date,gHit, rnum ,blind FROM (SELECT ROW_NUMBER() OVER (ORDER BY GPIDX DESC) AS rnum,GPIDX,SUBJECT,CONTENT,CHATURL,PROGIDX,ID,MAXUSER,CURRUSER,DEADLINE,GPCTGIDX,reg_date,gHit,blind FROM groupPurchase
WHERE blind='n'
AND GPCTGIDX=1
AND GPCTGIDX=2
)
WHERE rnum BETWEEN #{param1} AND #{param2}

SELECT GPIDX,SUBJECT,CONTENT,CHATURL,PROGIDX,ID,MAXUSER,CURRUSER,DEADLINE,GPCTGIDX,reg_date,gHit, rnum ,blind FROM (SELECT ROW_NUMBER() OVER (ORDER BY GPIDX DESC) AS rnum,GPIDX,SUBJECT,CONTENT,CHATURL,PROGIDX,ID,MAXUSER,CURRUSER,DEADLINE,GPCTGIDX,reg_date,gHit,blind FROM groupPurchase
WHERE blind='n' AND subject LIKE '%'||#{param4}||'%' OR content LIKE '%'||#{param4}||'%' AND subject LIKE '%'||#{param4}||'%' AND content LIKE '%'||#{param4}||'%' AND id LIKE '%'||#{param4}||'%' )
WHERE rnum BETWEEN #{param1} AND #{param2}
